let Room = function(){
  this.gen = null;
  this.seed = null;
  this.visited = false;
  this.loc = {x:null,y:null};
  this.backdrop = game.artist.images['levelBackdrop']; //image
  this.hasBeenSetup = false;
  this.actors = [];
  this.particles = [];
  this.enemies = [];
  this.roomSize = {x: game.width, y: game.height};
  this.name = 'Starting Room';


  this.directions = {
    left: null,
    up: null,
    down: null,
    right: null
  };

  this.setup = function(){
    // ensures only one setup
    if(this.hasBeenSetup){
      return;
    }

    this.gen = new RNG(this.seed);


    this.hasBeenSetup = true;
    //this is where room layout happens:
    let lists = game.parser.parse(this.layout);
    this.actors = lists[0];
    this.enemies = lists[1];

    // set exit
    let keys = Object.keys(this.directions);
    let done = false;
    keys.forEach(k=>{
        let pos = {x: 0, y: 0};
        if(this.directions[k] != null){
          switch(k){
            case 'left':
              pos.y = game.height / 2;
              pos.x += 48;
              break;
            case 'right':
              pos.y = game.height / 2;
              pos.x = game.width - 48;
              break;
            case 'up':
              pos.x = game.width / 2;
              pos.y += 48;
              break;
            case 'down':
              pos.x = game.width / 2;
              pos.y = game.height - 48;
              break;
            default:
            console.log('unexpected direction', direction);
          }
          this.actors.push(new Obstacle(pos, 'exit', 'exit', 'open', [k, this]))
        }
        else{
          if(k == this.finalRoom){
            switch(k){
              case 'left':
                pos.y = game.height / 2;
                pos.x += 48;
                break;
              case 'right':
                pos.y = game.height / 2;
                pos.x = game.width - 48;
                break;
              case 'up':
                pos.x = game.width / 2;
                pos.y += 48;
                break;
              case 'down':
                pos.x = game.width / 2;
                pos.y = game.height - 48;
                break;
              default:
              console.log('unexpected direction', direction);
            }
            this.actors.push(new Obstacle(pos, 'exit', 'finalExit', 'finalDoorOpen', [k, this]));
          }
        }
      });


  }

  this.update = function(delta){
    this.particles = this.particles.filter(p => !p.dead);
    this.particles.forEach(p => p.update(delta,this));
    this.enemies.forEach(e => e.update(delta, this));
    game.player.update(delta, this);
    this.actors.forEach(a => a.update(delta, this));
  }

  this.draw = function(){
    // Draw backdrop
    game.artist.drawImage(this.backdrop, 0, 0, game.width, game.height );
    this.particles.forEach(p => p.draw());
    this.actors.forEach(a => a.draw());

    //draw enemies
    //draw player
    this.enemies.forEach(e => e.draw());
    game.player.draw();


    if(this.isStart){
      this.backdrop = game.artist.images['instructionsBackdrop'];
    }

    game.artist.writeText(this.name, 32, 32, 18,'white');
    game.artist.drawCircle(game.width - 64, game.height - 64, 32, 'white')

    if(game.player.altWeapon){
       game.player.altWeapon.sprite.draw({x: game.width - 64, y: game.height - 64}, 64, 64, -Math.PI/8);
     }
    let maxX = 0;
    let maxY = 0;
    game.map.rooms.forEach(r=>{
      if(r.loc.x > maxX){
        maxX = r.loc.x;
     }
     if(r.loc.y < maxY){
       maxY = r.loc.y;
     }
    });

    if(game.player.curWeapon){
     let max = WEAPONS.templates[game.player.curWeapon.name].numShots;
     let color = 'white';
     let perc = Math.round(game.player.curWeapon.numShots/max * 100);
     if(perc < 10){
       color = game.artist.randColor();
     }else if(perc < 20){
       color = 'red';
     }else if(perc < 50){
       color = 'orange';
     }else if(perc < 80){
       color = 'yellow';
     }



     game.artist.writeText(`Stability: ${perc}%`, 32, game.height - 64, 24, color);
    }
    let healthColor = 'white';
    if(game.player.health < 10){
      healthColor = game.artist.randColor();
    }else if(game.player.health < 20){
      healthColor = 'red';
    }else if(game.player.health < 50){
      healthColor = 'orange';
    }else if(game.player.health < 80){
      healthColor = 'yellow';
    }
    game.artist.writeText(`Health: ${game.player.health}`, 32, game.height- 96,24,healthColor);

    game.map.rooms.filter(r=> r.visited).forEach(r=>{
      let pos = {x: r.loc.x, y: r.loc.y};
      pos.x *= 25;
      pos.y *= 25;

      pos.x += game.width - (maxX + 3) * 25;
      pos.y += (maxY - 3) * -25;
      if(r.id == this.id){
         game.artist.drawRect(pos.x, pos.y, 20, 20, '#AA9DA0');
      }else{
         game.artist.drawRect(pos.x, pos.y, 20, 20, '#86777A');
      }
   });
   game.map.rooms.filter(r=> r.finalRoom).forEach(r=>{
     if(r.visited){
       let pos = {x: r.loc.x, y: r.loc.y};
       pos.x *= 25;
       pos.y *= 25;

       pos.x += game.width - (maxX + 3) * 25;
       pos.y += (maxY - 3) * -25;
       if(r.id == this.id){
          game.artist.drawRect(pos.x, pos.y, 20, 20, '#ee2654');
       }else{
          game.artist.drawRect(pos.x, pos.y, 20, 20, '#900e28');
       }
     }
   })
   let roomsToClear = 0;
   game.map.rooms.forEach(r=>{
     let x = r.enemies.filter(e=>{
       return !e.dead;
     })
     if(x.length > 0){
       roomsToClear++;
     }
   });
   game.artist.drawRect(0,game.height - 28,8*32, 32,'black');
   game.artist.writeText(`Rooms To Clear: ${roomsToClear}`, 32, game.height- 25,24,'white');

 }

  this.attach = function(direction, newRoom, map){
    this.directions[direction] = newRoom;

    let oppDir = null;
    switch(direction){
      case 'left':
        newRoom.loc.x = this.loc.x - 1;
        newRoom.loc.y = this.loc.y;
        oppDir = 'right';
        break;
      case 'right':
        newRoom.loc.x = this.loc.x + 1;
        newRoom.loc.y = this.loc.y;
        oppDir = 'left';
        break;
      case 'up':
        newRoom.loc.x = this.loc.x;
        newRoom.loc.y = this.loc.y - 1;
        oppDir = 'down';
        break;
      case 'down':
        newRoom.loc.x = this.loc.x;
        newRoom.loc.y = this.loc.y + 1;
        oppDir = 'up';
        break;
      default:
        console.log('FUCKING NULL DIRECTION DAWG');
        return false;
    }
    newRoom.directions[oppDir] = this;

    let adjRooms = map.rooms.filter(r => {
      let xOffOne = r.loc.x == newRoom.loc.x + 1
       || r.loc.x == newRoom.loc.x - 1;
      let xSame = r.loc.x == newRoom.loc.x;
      let yOffOne = r.loc.y == newRoom.loc.y + 1
       || r.loc.y == newRoom.loc.y - 1;
      let ySame = r.loc.y == newRoom.loc.y;
      return (xOffOne && ySame) || (yOffOne && xSame);
    })
    // console.log(newRoom.loc);
    if(adjRooms.length > 4){
      console.log("YOU FUCKED UP ATTACHING THINGS");
    }

    adjRooms.forEach(r => {
      if(r.id == this.id){
        return;
      }
      //right
      if(r.loc.x > newRoom.loc.x){
        newRoom.directions['right'] = r;
        r.directions['left'] = newRoom;
      }
      //left
      if(r.loc.x < newRoom.loc.x){
        newRoom.directions['left'] = r;
        r.directions['right'] = newRoom;
      }
      //down
      if(r.loc.y > newRoom.loc.y){
        newRoom.directions['down'] = r;
        r.directions['up'] = newRoom;
      }
      //up
      if(r.loc.y < newRoom.loc.y){
        newRoom.directions['up'] = r;
        r.directions['down'] = newRoom;
      }
    })

    return true;
  }
}


